import React, { Fragment, useEffect, useState, useReducer} from "react";
import API from "../utils/API";
import Wrapper from "../components/Wrapper/Wrapper";
import Nav from "../components/Nav/Nav";
import Search from "../components/Search/Search";
import Header from "../components/Header/Header";
import Footer from "../components/Footer/Footer";
import BookContainer from "../components/BookContainer/BookContainer";
import BookItem from "../components/BookItem/BookItem";
import BookItemButton from "../components/Buttons/BookItemButton";
import ViewMoreButton from "../components/Buttons/ViewMoreButton";
import bookReducer from "../hooks/bookReducer";

const BookSearch = () => {
    
    const intialState = [];
    const [searchQuery, setSearchQuery] = useState("");
    const [hasSubmit, setHasSubmit] = useState(false);

    const viewMoreBooks = () => {

    };

    useEffect(() => {
        if(hasSubmit){
            
            setHasSubmit(false);
        }
    }, [hasSubmit]);

    return (
        <Fragment>
            <Wrapper>
                <Header>
                    <Nav>
                        <Search searchQuery={searchQuery} setSearchQuery={setSearchQuery} setHasSubmit={setHasSubmit}/>
                    </Nav>
                </Header>
                <BookContainer containerTitle="Results">
                    <BookItem>
                        <BookItemButton name="View Book"/>
                        <BookItemButton name="Save Book"/>
                    </BookItem>
                    <ViewMoreButton viewMoreBooks={viewMoreBooks}/>
                </BookContainer>
            </Wrapper>
            <Footer />
        </Fragment>
    );
}

export default BookSearch;
