import API from "../utils/API";

const search = async (query, index) => {
    const {data} = await API.searchBook(query, index);

    return {data};
};

const bookReducer = (state, action) => {
    switch(action.type){
        case "search": return search(action.query, action.index).then(({data}) => {return {books: [data]}});
        default: return state;
    }
};

export default bookReducer;
