import API from "../utils/API";

const bookReducer = async (state, action) => {
    switch(action.type){
        case "search": {
            const {data} = await API.searchBook(action.query, action.index);
            console.log(data)
            return {books: [...data]}
        };
        default: return state;
    }
};

export default bookReducer;
