const sortByDate = (state) => {
    return state.books.sort((a, b) => {
        if(a.volumeInfo.publishedDate > b.volumeInfo.publishedDate){
            return -1 
        } else {
            return 1
        }
    }); 
}

const sortByTitle = (state) => {
    return state.books.sort((a, b) => {
        if(a.volumeInfo.title > b.volumeInfo.title){
            return -1 
        } else {
            return 1
        }
    }); 
}

const bookReducer = (state, action) => {
    switch(action.type){
        case "search": return {books: [...state.books,...action.payload]};
        case "clear": return {books: []};
        case "sortByDate": return {books: sortByDate(state)};
        case "sortByTitle": return {books: sortByTitle(state)};
        default: return state;
    }
};

export default bookReducer;
