import React, { Fragment, useEffect, useState, useReducer} from "react";
import API from "../utils/API";
import Wrapper from "../components/Wrapper/Wrapper";
import Nav from "../components/Nav/Nav";
import Search from "../components/Search/Search";
import Header from "../components/Header/Header";
import Footer from "../components/Footer/Footer";
import BookContainer from "../components/BookContainer/BookContainer";
import BookItem from "../components/BookItem/BookItem";
import BookItemButton from "../components/Buttons/BookItemButton";
import ViewMoreButton from "../components/Buttons/ViewMoreButton";
import bookReducer from "../hooks/bookReducer";

const BookSearch = () => {
    
    const initalState = {books: []};
    const [state, dispatch] = useReducer(bookReducer, initalState);
    const [searchQuery, setSearchQuery] = useState("");
    const [currentIndex, setCurrentIndex] = useState(0);
    const [displayViewBtn, setdisplayViewBtn] = useState(false);
    const [isSubmit, setIsSubmit] = useState(false);

    const getMoreBooks = () => {
        setIsSubmit(true)
    };

    useEffect(() => {
        if(isSubmit){
            dispatch({type: "search", query: searchQuery, index: currentIndex});
        }
        setIsSubmit(false);
    }, [isSubmit])

    useEffect(()=>{
        console.log(state);
    }, [state])

    return (
        <Fragment>
            <Wrapper>
                <Header>
                    <Nav>
                        <Search 
                            searchQuery={searchQuery} 
                            setSearchQuery={setSearchQuery} 
                            getMoreBooks={getMoreBooks}
                            setCurrentIndex={setCurrentIndex}
                            setdisplayViewBtn={setdisplayViewBtn}
                        />
                    </Nav>
                </Header>
                <BookContainer containerTitle="Results">
                    <BookItem>
                        <BookItemButton name="View Book"/>
                        <BookItemButton name="Save Book"/>
                    </BookItem>
                    {displayViewBtn && <ViewMoreButton getMoreBooks={getMoreBooks}/>}
                </BookContainer>
            </Wrapper>
            <Footer />
        </Fragment>
    );
}

export default BookSearch;
