        <form className="search" onSubmit={handleSubmit}>
            <input 
                ref={inputEl}
                type="text" 
                className="search__input" 
                placeholder="Search for a book." 
                value={searchVal}
            />
            <button className="search__btn">
               <MagnifyingIcon/>
            </button>
        </form>
    );
}

export default Search;
