import React from "react";
import"./style.css";

const BookItem = ({children, book}) => {
    const {authors, title, imageLinks, description} = book.volumeInfo;
    return  (
        <div className="book-item">
            <img className="book-item__img" src={imageLinks.thumbnail ? imageLinks.thumbnail : eh}></img>
            <div className="book-item__description">
                <p className="book-item__title">{title}</p>
                <p className="book-item__author">
                    {authors && authors.length > 1 ? `Authors: ${authors.join(", ")}`:`Author: ${authors}`}
                </p>
                <p className="book-item__synopsis">{description ? description : "No description available."}</p>
                <div className="book-item__btn-container">
                    {children}
                </div>
            </div>
        </div>
    );
}

export default BookItem;
