import API from "../utils/API";

const search = async (query, index) => {
    const booksList = await API.searchBook(query, index);
    return booksList;
};

const bookReducer = (state, action) => {
    switch(action.type){
        case "search": return {books: async () => await search(action.query, action.index)}
        default: return state;
    }
};

export default bookReducer;
