import axios from "axios";

export default {
  // Searches for book https://www.googleapis.com/books/v1/volumes?q=${query}&startIndex=${currentIndex}&maxResults=10
  searchBook: function(query, index) {
    const queryString = {query: query, index, index}
    return axios.get("/api/books/search/" + queryString)
  },
  // Gets all books
  getBooks: function() {
    return axios.get("/api/books");
  },
  // Gets the book with the given id
  getBook: function(id) {
    return axios.get("/api/books/" + id);
  },
  // Deletes the book with the given id
  deleteBook: function(id) {
    return axios.delete("/api/books/" + id);
  },
  // Saves a book to the database
  saveBook: function(bookData) {
    return axios.post("/api/books", bookData);
  }
};
