import API from "../utils/API";

const search = async (query, index) => {
    const {data} = await API.searchBook(query, index);
        
    return data;
};

const bookReducer = async (state, action) => {
    switch(action.type){
        case "search": {
            const books = await search(action.query, action.index);
            return books
        };
        default: return state;
    }
};

export default bookReducer;
