import API from "../utils/API";

const search = async (index, query) => {
        const response = await API.searchBook(query, index);
};

const bookReducer = (state, action) => {
    console.log(action.query + "q");
    switch(action.type){
        case "search": return {books: search(action.query, action.index)}
        default: return state;
    }
};

export default bookReducer;
