import React, { Fragment, useEffect, useState, useReducer} from "react";
import API from "../utils/API";
import Wrapper from "../components/Wrapper/Wrapper";
import Nav from "../components/Nav/Nav";
import Search from "../components/Search/Search";
import Header from "../components/Header/Header";
import Footer from "../components/Footer/Footer";
import BookContainer from "../components/BookContainer/BookContainer";
import BookItem from "../components/BookItem/BookItem";
import BookItemButton from "../components/Buttons/BookItemButton";
import ViewMoreButton from "../components/Buttons/ViewMoreButton";
import bookReducer from "../hooks/bookReducer";

const BookSearch = () => {
    
    const initalState = {books: []};
    const maxResultLength = 10;
    const [state, dispatch] = useReducer(bookReducer, initalState);
    const [searchQuery, setSearchQuery] = useState("");
    const [currentIndex, setCurrentIndex] = useState(0);
    const [displayViewBtn, setdisplayViewBtn] = useState(false);
    const [findBooks, setfindBooks] = useState(false);

    const getMoreBooks = () => {
        setfindBooks(true)
    };

    useEffect(() => {
        if(findBooks){
            API.searchBook(searchQuery, currentIndex)
            .then(({data}) => {
                dispatch({type: "search", payload: data})
                setCurrentIndex(currentIndex += 10)
            });
        }
        setfindBooks(false);

    }, [findBooks])

    useEffect(() => {
        setCurrentIndex(0);
        dispatch({type: "clear"})
    }, [searchQuery])

    return (
        <Fragment>
            <Wrapper>
                <Header>
                    <Nav>
                        <Search 
                            searchQuery={searchQuery} 
                            setSearchQuery={setSearchQuery} 
                            getMoreBooks={getMoreBooks}
                            setCurrentIndex={setCurrentIndex}
                            setdisplayViewBtn={setdisplayViewBtn}
                        />
                    </Nav>
                </Header>
                <BookContainer containerTitle="Results">
                    {state.books.length > 0 && state.books.map((book) => {
                        return (
                      
                            <BookItem book={book}>
                                <BookItemButton name="View Book"/>
                                <BookItemButton name="Save Book"/>
                            </BookItem>
                        );
                    })}
                    {displayViewBtn && state.books.length > 0 && <ViewMoreButton getMoreBooks={getMoreBooks} setCurrentIndex={setCurrentIndex}/>}
                </BookContainer>
            </Wrapper>
            <Footer />
        </Fragment>
    );
}

export default BookSearch;
